import request from '@/utils/request'

// 获取部门树
export function getDeptTree(params) {
  // 确保提供默认的分页参数
  const defaultParams = {
    pageSize: 100,
    pageNum: 1
  }

  return request({
    url: '/sysDept/list',
    method: 'get',
    params: params || defaultParams
  })
}

// 获取部门详情
export function getDeptById(id) {
  return request({
    url: '/sysDept/getById',
    method: 'get',
    params: { id }
  })
}

// 添加部门
export function addDept(data) {
  return request({
    url: '/sysDept/add',
    method: 'post',
    data
  })
}

// 更新部门
export function updateDept(data) {
  return request({
    url: '/sysDept/update',
    method: 'post',
    data
  })
}

// 删除部门
export function deleteDept(id) {
  return request({
    url: '/sysDept/delete',
    method: 'post',
    params: { id }
  })
}
