import request from '@/utils/request'

// 获取订单列表
export function getOrderList(params) {
  return request({
    url: '/order/list',
    method: 'get',
    params
  })
}

// 获取订单详情
export function getOrderById(id) {
  return request({
    url: '/order/getById',
    method: 'get',
    params: { id }
  })
}

// 更新订单状态
export function updateOrderStatus(data) {
  return request({
    url: '/order/updateStatus',
    method: 'post',
    data
  })
}

// 获取订单评价列表
export function getOrderCommentList(params) {
  return request({
    url: '/order/list',
    method: 'get',
    params
  })
}

// 获取评价详情
export function getCommentById(id) {
  return request({
    url: '/order/getById',
    method: 'get',
    params: { id }
  })
}

// 回复评价
export function replyComment(data) {
  return request({
    url: '/order/reply',
    method: 'post',
    data
  })
}
