import request from '@/utils/request'

// 获取优惠券列表
export function getCouponList(params) {
  return request({
    url: '/coupon/list',
    method: 'get', 
    params
  })
}

// 获取优惠券详情
export function getCouponById(id) {
  return request({
    url: '/coupon/detail',
    method: 'get',
    params: { id }
  })
}

// 创建优惠券
export function createCoupon(data) {
  return request({
    url: '/coupon/add',
    method: 'post',
    data
  })
}

// 更新优惠券
export function updateCoupon(data) {
  return request({
    url: '/coupon/update',
    method: 'post',
    data
  })
}

// 删除优惠券
export function deleteCoupon(id) {
  return request({
    url: '/coupon/delete',
    method: 'post',
    params: { id }
  })
}

// 启用/禁用优惠券
export function changeCouponStatus(data) {
  return request({
    url: '/coupon/changeStatus',
    method: 'post',
    data
  })
}
