import request from '@/utils/request'
import qs from 'qs'

// 获取优惠券列表
export function getCouponList(params) {
  return request({
    url: '/coupon/getCouponInfo',
    method: 'get', 
    params
  })
}

// 获取优惠券详情
export function getCouponByName(name) {
  return request({
    url: '/coupon/getCouponInfoByName',
    method: 'get',
    params: { name }
  })
}

// 创建优惠券
export function createCoupon(data) {
  // 处理日期格式
  if (data.expireTime && data.expireTime instanceof Date) {
    data.expireTime = data.expireTime.toISOString().split('.')[0].replace('T', ' ')
  }

  return request({
    url: '/coupon/addCouponInfo',
    method: 'get', // 改为GET请求，参数通过params传递
    params: data
  })
}

// 更新优惠券
export function updateCoupon(data) {
  // 处理日期格式
  if (data.expireTime && data.expireTime instanceof Date) {
    data.expireTime = data.expireTime.toISOString().split('.')[0].replace('T', ' ')
  }

  return request({
    url: '/coupon/updateCouponInfo',
    method: 'get', // 改为GET请求，参数通过params传递
    params: data
  })
}

// 删除优惠券
export function deleteCoupon(id) {
  return request({
    url: '/coupon/removeCouponInfo',
    method: 'get', // 改为GET请求
    params: { id }
  })
}

// 启用/禁用优惠券
export function changeCouponStatus(data) {
  return request({
    url: '/coupon/updateCouponInfo',
    method: 'post',
    headers: {
      'Content-Type': 'application/x-www-form-urlencoded'
    },
    data: qs.stringify(data)
  })
}

// 获取客户优惠券列表
export function getCustomerCouponList(params) {
  return request({
    url: '/coupon/getCustomerCouponInfoByUserId',
    method: 'get',
    params
  })
}
