import { ref, reactive, onMounted } from 'vue';
import { ElMessage, ElMessageBox } from 'element-plus';
import { getAllUsers, updateUser, deleteUser, getUserById, getUserLoginLog, getUserOperLog } from '@/api/user';
import { getDeptTree } from '@/api/dept';
import { getPostList } from '@/api/post';

// 查询参数

export default {
  __name: 'SysUser',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const queryParams = reactive({
      pageNum: 1,
      pageSize: 10,
      username: undefined,
      name: undefined,
      phone: undefined,
      deptId: undefined,
      postId: undefined,
      status: undefined,
      deptName: undefined,
      postName: undefined
    });

    // 表格数据和加载状态
    const userList = ref([]);
    const loading = ref(false);
    const total = ref(0);

    // 部门和岗位选项
    const deptOptions = ref([]);
    const postOptions = ref([]);

    // 编辑对话框相关
    const dialogVisible = ref(false);
    const userFormRef = ref(null);
    const userForm = reactive({
      id: '',
      username: '',
      name: '',
      phone: '',
      deptId: undefined,
      postId: undefined,
      status: 1,
      description: '',
      password: '',
      headUrl: ''
    });

    // 登录日志相关
    const loginLogDialogVisible = ref(false);
    const loginLogList = ref([]);
    const loginLogTotal = ref(0);
    const logLoading = ref(false);
    const loginLogQuery = reactive({
      pageNum: 1,
      pageSize: 10,
      username: '',
      minAccessTime: undefined,
      maxAccessTime: undefined
    });

    // 操作日志相关
    const operLogDialogVisible = ref(false);
    const operLogList = ref([]);
    const operLogTotal = ref(0);
    const operLogLoading = ref(false);
    const operLogQuery = reactive({
      pageNum: 1,
      pageSize: 10,
      operName: '',
      deptName: '',
      status: undefined,
      operatorType: undefined,
      businessType: undefined,
      requestMethod: undefined,
      minOperTime: undefined,
      maxOperTime: undefined
    });

    // 表单验证规则
    const rules = {
      name: [{
        required: true,
        message: '请输入姓名',
        trigger: 'blur'
      }],
      phone: [{
        required: true,
        message: '请输入手机号',
        trigger: 'blur'
      }, {
        pattern: /^1[3-9]\d{9}$/,
        message: '请输入正确的手机号',
        trigger: 'blur'
      }]
    };

    // 查询用户列表
    const getList = async () => {
      try {
        loading.value = true;
        const res = await getAllUsers(queryParams);
        console.log('用户列表响应:', res);
        userList.value = res.data.items || [];
        total.value = res.data.total || 0;
      } catch (error) {
        console.error('获取用户列表失败', error);
      } finally {
        loading.value = false;
      }
    };

    // 查询按钮
    const handleQuery = () => {
      queryParams.pageNum = 1;
      getList();
    };

    // 重置查询
    const resetQuery = () => {
      // 重置查询表单
      queryParams.username = undefined;
      queryParams.name = undefined;
      queryParams.phone = undefined;
      queryParams.deptName = undefined;
      queryParams.pageNum = 1;
      getList();
    };

    // 编辑用户
    const handleEdit = async row => {
      try {
        console.log('开始编辑用户:', row);

        // 先根据ID获取最新的完整用户信息
        const userId = row.id;
        userForm.password = ''; // 初始化密码字段为空
        userForm.headUrl = ''; // 初始化头像URL字段为空

        // 尝试获取完整用户信息
        try {
          const userDetail = await getUserById(userId);
          console.log('获取到完整用户信息:', userDetail);
          if (userDetail && userDetail.code === 200 && userDetail.data) {
            // 使用服务器返回的最新数据
            const userData = userDetail.data;
            userForm.id = userData.id;
            userForm.username = userData.username;
            userForm.name = userData.name;
            userForm.phone = userData.phone;
            userForm.deptId = userData.deptId;
            userForm.postId = userData.postId;
            userForm.status = userData.status;
            userForm.description = userData.description;
            userForm.password = userData.password || ''; // 保留原密码
            userForm.headUrl = userData.headUrl || ''; // 保留原头像URL
          } else {
            // 回退到使用表格中的数据
            userForm.id = row.id;
            userForm.username = row.username;
            userForm.name = row.name;
            userForm.phone = row.phone;
            userForm.deptId = row.deptId;
            userForm.postId = row.postId;
            userForm.status = row.status || 1;
            userForm.description = row.description || '';
          }
        } catch (error) {
          console.error('获取用户详情失败，使用表格数据:', error);
          // 回退到使用表格中的数据
          userForm.id = row.id;
          userForm.username = row.username;
          userForm.name = row.name;
          userForm.phone = row.phone;
          userForm.deptId = row.deptId;
          userForm.postId = row.postId;
          userForm.status = row.status || 1;
          userForm.description = row.description || '';
        }

        // 显示对话框
        dialogVisible.value = true;

        // 获取部门和岗位选项
        try {
          await Promise.all([getDeptOptions(), getPostOptions()]);
        } catch (error) {
          console.error('获取选项数据失败', error);
        }
      } catch (error) {
        console.error('编辑用户出错:', error);
        ElMessage.error('加载用户信息失败');
      }
    };

    // 删除用户
    const handleDelete = row => {
      ElMessageBox.confirm(`确定要删除用户 ${row.username} 吗?`, '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(async () => {
        try {
          await deleteUser(row.id);
          ElMessage.success('删除成功');
          getList();
        } catch (error) {
          ElMessage.error('删除失败');
        }
      }).catch(() => {});
    };

    // 获取部门选项
    const getDeptOptions = async () => {
      try {
        const res = await getDeptTree();
        console.log('获取部门选项响应:', res);

        // 处理不同的数据格式
        if (res.data && res.data.items) {
          // 如果是分页格式
          deptOptions.value = flattenDeptTree(buildDeptTree(res.data.items));
        } else if (res.data && Array.isArray(res.data)) {
          // 如果直接是数组格式
          deptOptions.value = flattenDeptTree(res.data);
        } else {
          console.warn('未识别的部门数据格式');
          deptOptions.value = [];
        }
      } catch (error) {
        console.error('获取部门选项失败', error);
        ElMessage.warning('获取部门选项失败，请稍后再试');
        deptOptions.value = [];
      }
    };

    // 构建部门树结构
    const buildDeptTree = depts => {
      // 创建映射表
      const deptMap = {};
      depts.forEach(dept => {
        dept.children = [];
        deptMap[dept.id] = dept;
      });

      // 构建树结构
      const result = [];
      depts.forEach(dept => {
        if (dept.parentId && deptMap[dept.parentId]) {
          deptMap[dept.parentId].children.push(dept);
        } else {
          result.push(dept);
        }
      });
      return result;
    };

    // 扁平化部门树结构
    const flattenDeptTree = deptTree => {
      if (!deptTree || !Array.isArray(deptTree)) {
        console.warn('部门树结构无效', deptTree);
        return [];
      }
      const result = [];
      const flatten = items => {
        items.forEach(item => {
          if (item && item.id) {
            result.push({
              id: item.id,
              name: item.name
            });
            if (item.children && item.children.length > 0) {
              flatten(item.children);
            }
          }
        });
      };
      flatten(deptTree);
      return result;
    };

    // 获取岗位选项
    const getPostOptions = async () => {
      try {
        // 使用默认的分页参数
        const params = {
          pageSize: 100,
          pageNum: 1
        };
        const res = await getPostList(params);
        console.log('获取岗位选项响应:', res);

        // 处理不同的数据格式
        if (res.data && res.data.items) {
          // 分页格式
          postOptions.value = (res.data.items || []).map(item => ({
            id: item.id,
            name: item.name
          }));
        } else if (res.data && Array.isArray(res.data)) {
          // 直接是数组格式
          postOptions.value = res.data.map(item => ({
            id: item.id,
            name: item.name
          }));
        } else {
          console.warn('未识别的岗位数据格式');
          postOptions.value = [];
        }
      } catch (error) {
        console.error('获取岗位选项失败', error);
        ElMessage.warning('获取岗位选项失败，请稍后再试');
        postOptions.value = [];
      }
    };

    // 提交表单
    const submitForm = () => {
      userFormRef.value.validate(async valid => {
        if (valid) {
          try {
            console.log('表单验证通过，准备提交数据...');

            // 确保包含所有必要字段
            const submitData = {
              id: userForm.id,
              username: userForm.username,
              password: userForm.password || '',
              // 包含原密码
              name: userForm.name,
              phone: userForm.phone,
              headUrl: userForm.headUrl || '',
              // 包含原头像
              deptId: userForm.deptId,
              postId: userForm.postId,
              status: userForm.status,
              description: userForm.description || ''
            };
            console.log('即将提交的用户数据:', submitData);

            // 显示加载状态
            loading.value = true;

            // 使用API
            const result = await updateUser(submitData);
            console.log('更新用户返回结果:', result);

            // 结束加载状态
            loading.value = false;

            // 根据后端返回的boolean判断结果
            if (result === true) {
              ElMessage.success('更新成功');
              dialogVisible.value = false;
              getList(); // 刷新列表
            } else {
              throw new Error('服务器返回失败状态');
            }
          } catch (error) {
            loading.value = false;
            console.error('更新用户失败:', error);
            ElMessage.error('更新失败: ' + (error.message || '未知错误'));
          }
        }
      });
    };

    // 分页操作
    const handleSizeChange = size => {
      queryParams.pageSize = size;
      getList();
    };
    const handleCurrentChange = page => {
      queryParams.pageNum = page;
      getList();
    };

    // 查看登录日志
    const viewLoginLog = async row => {
      try {
        loginLogDialogVisible.value = true;
        logLoading.value = true;

        // 显示对话框后再加载日志，以便用户看到加载状态
        loginLogQuery.username = row.username;
        console.log('准备查询用户登录日志:', row.username);
        await getLoginLogList();
      } catch (error) {
        console.error('显示登录日志对话框失败:', error);
        ElMessage.error('无法加载登录日志');
        loginLogDialogVisible.value = false;
      } finally {
        logLoading.value = false;
      }
    };

    // 获取登录日志列表
    const getLoginLogList = async () => {
      try {
        logLoading.value = true;
        console.log('发送登录日志查询参数:', loginLogQuery);
        const res = await getUserLoginLog(loginLogQuery);
        console.log('登录日志响应:', res);

        // 处理响应
        if (res && res.data) {
          loginLogList.value = res.data.items || [];
          loginLogTotal.value = res.data.total || 0;
          if (loginLogList.value.length === 0) {
            ElMessage.info('该用户没有登录日志记录');
          }
        } else {
          loginLogList.value = [];
          loginLogTotal.value = 0;
          ElMessage.info('未获取到日志数据');
        }
      } catch (error) {
        console.error('获取登录日志失败', error);
        ElMessage.error('获取登录日志出错: ' + (error.message || '未知错误'));
        loginLogList.value = [];
        loginLogTotal.value = 0;
      } finally {
        logLoading.value = false;
      }
    };

    // 登录日志分页操作
    const handleLogSizeChange = size => {
      loginLogQuery.pageSize = size;
      getLoginLogList();
    };
    const handleLogCurrentChange = page => {
      loginLogQuery.pageNum = page;
      getLoginLogList();
    };

    // 查看操作日志
    const viewOperLog = async row => {
      try {
        operLogDialogVisible.value = true;
        operLogLoading.value = true;

        // 显示对话框后再加载日志，以便用户看到加载状态
        operLogQuery.operName = row.username;
        console.log('准备查询用户操作日志:', row.username);
        await getOperLogList();
      } catch (error) {
        console.error('显示操作日志对话框失败:', error);
        ElMessage.error('无法加载操作日志');
        operLogDialogVisible.value = false;
      } finally {
        operLogLoading.value = false;
      }
    };

    // 获取操作日志列表
    const getOperLogList = async () => {
      try {
        operLogLoading.value = true;
        console.log('发送操作日志查询参数:', operLogQuery);
        const res = await getUserOperLog(operLogQuery);
        console.log('操作日志响应:', res);

        // 处理响应
        if (res && res.data) {
          operLogList.value = res.data.items || [];
          operLogTotal.value = res.data.total || 0;
          if (operLogList.value.length === 0) {
            ElMessage.info('该用户没有操作日志记录');
          }
        } else {
          operLogList.value = [];
          operLogTotal.value = 0;
          ElMessage.info('未获取到日志数据');
        }
      } catch (error) {
        console.error('获取操作日志失败', error);
        ElMessage.error('获取操作日志出错: ' + (error.message || '未知错误'));
        operLogList.value = [];
        operLogTotal.value = 0;
      } finally {
        operLogLoading.value = false;
      }
    };

    // 操作日志分页操作
    const handleOperLogSizeChange = size => {
      operLogQuery.pageSize = size;
      getOperLogList();
    };
    const handleOperLogCurrentChange = page => {
      operLogQuery.pageNum = page;
      getOperLogList();
    };

    // 页面加载时获取数据
    onMounted(() => {
      getList();
    });
    const __returned__ = {
      queryParams,
      userList,
      loading,
      total,
      deptOptions,
      postOptions,
      dialogVisible,
      userFormRef,
      userForm,
      loginLogDialogVisible,
      loginLogList,
      loginLogTotal,
      logLoading,
      loginLogQuery,
      operLogDialogVisible,
      operLogList,
      operLogTotal,
      operLogLoading,
      operLogQuery,
      rules,
      getList,
      handleQuery,
      resetQuery,
      handleEdit,
      handleDelete,
      getDeptOptions,
      buildDeptTree,
      flattenDeptTree,
      getPostOptions,
      submitForm,
      handleSizeChange,
      handleCurrentChange,
      viewLoginLog,
      getLoginLogList,
      handleLogSizeChange,
      handleLogCurrentChange,
      viewOperLog,
      getOperLogList,
      handleOperLogSizeChange,
      handleOperLogCurrentChange,
      ref,
      reactive,
      onMounted,
      get ElMessage() {
        return ElMessage;
      },
      get ElMessageBox() {
        return ElMessageBox;
      },
      get getAllUsers() {
        return getAllUsers;
      },
      get updateUser() {
        return updateUser;
      },
      get deleteUser() {
        return deleteUser;
      },
      get getUserById() {
        return getUserById;
      },
      get getUserLoginLog() {
        return getUserLoginLog;
      },
      get getUserOperLog() {
        return getUserOperLog;
      },
      get getDeptTree() {
        return getDeptTree;
      },
      get getPostList() {
        return getPostList;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
