import request from '@/utils/request'

// 获取部门列表
export function getDeptList(params) {
  return request({
    url: '/sysDept/list',
    method: 'get',
    params
  })
}

// 获取部门树
export function getDeptTree() {
  // 尝试直接使用树形接口
  return request({
    url: '/sysDept/tree',
    method: 'get'
  }).catch(error => {
    console.error('获取部门树失败，尝试使用列表接口', error)
    // 如果树形接口失败，回退到使用list接口
    return getDeptList({
      pageSize: 100, // 设置较大的页大小以获取所有部门
      pageNum: 1
    })
  })
}

// 获取部门详情
export function getDeptById(id) {
  return request({
    url: '/sysDept/getById',
    method: 'get',
    params: { id }
  })
}

// 添加部门
export function addDept(data) {
  return request({
    url: '/sysDept/add',
    method: 'post',
    data
  })
}

// 更新部门
export function updateDept(data) {
  return request({
    url: '/sysDept/update',
    method: 'post',
    data
  })
}

// 删除部门
export function deleteDept(id) {
  return request({
    url: '/sysDept/delete',
    method: 'post',
    params: { id }
  })
}
