import request from '@/utils/request'

// 获取订单列表
export function getOrderList(params) {
  return request({
    url: '/order/getOrderInfo',
    method: 'post',
    params
  })
}

// 获取订单详情
export function getOrderByOrderNo(orderNo) {
  return request({
    url: '/order/getByOrderNo',
    method: 'post',
    params: { orderNo }
  })
}

// 创建订单
export function createOrder(data) {
  return request({
    url: '/order/add',
    method: 'post',
    data
  })
}

// 更新订单
export function updateOrder(data) {
  return request({
    url: '/order/update',
    method: 'post',
    data
  })
}

// 删除订单
export function deleteOrder(orderNo) {
  return request({
    url: '/order/delete',
    method: 'post',
    params: { orderNo }
  })
}

// 获取订单评价列表
export function getOrderCommentList(params) {
  return request({
    url: '/orderComment/list',
    method: 'get',
    params
  })
}

// 获取评价详情
export function getCommentById(id) {
  return request({
    url: '/orderComment/getOrderCommentInfoById',
    method: 'get',
    params: { id }
  })
}

// 回复评价
export function replyComment(data) {
  return request({
    url: '/orderComment/replyOrderComment',
    method: 'post',
    params: data
  })
}
