import request from '@/utils/request'
import qs from 'qs'

// 获取优惠券列表
export function getCouponList(params) {
  return request({
    url: '/coupon/getCouponInfo',
    method: 'get', 
    params
  })
}

// 获取优惠券详情
export function getCouponByName(name) {
  return request({
    url: '/coupon/getCouponInfoByName',
    method: 'get',
    params: { name }
  })
}

// 创建优惠券
export function createCoupon(data) {
  // 处理日期格式
  const submitData = { ...data }

  // 直接用表单数据提交，适应Spring MVC的对象绑定
  return request({
    url: '/coupon/addCouponInfo',
    method: 'post',
    headers: {
      'Content-Type': 'application/x-www-form-urlencoded'
    },
    data: qs.stringify(submitData, { allowDots: true })
  })
}

// 更新优惠券
export function updateCoupon(data) {
  // 创建新对象避免修改原始数据
  const submitData = { ...data }

  // 直接用表单数据提交
  return request({
    url: '/coupon/updateCouponInfo',
    method: 'post',
    headers: {
      'Content-Type': 'application/x-www-form-urlencoded'
    },
    data: qs.stringify(submitData, { allowDots: true })
  })
}

// 删除优惠券
export function deleteCoupon(id) {
  return request({
    url: '/coupon/removeCouponInfo',
    method: 'post',
    headers: {
      'Content-Type': 'application/x-www-form-urlencoded'
    },
    data: qs.stringify({ id })
  })
}

// 启用/禁用优惠券
export function changeCouponStatus(data) {
  return request({
    url: '/coupon/updateCouponInfo',
    method: 'post',
    headers: {
      'Content-Type': 'application/x-www-form-urlencoded'
    },
    data: qs.stringify(data)
  })
}

// 获取客户优惠券列表
export function getCustomerCouponList(params) {
  return request({
    url: '/coupon/getCustomerCouponInfoByUserId',
    method: 'get',
    params
  })
}
