import request from '@/utils/request'

// 登录接口
export function login(data) {
  return request({
    url: '/securityLogin/login',
    method: 'post',
    params: data
  })
}

// 获取用户信息
export function getUserInfo() {
  return request({
    url: '/securityLogin/getUserInfo',
    method: 'get'
  })
}

// 获取所有用户
export function getAllUsers(params) {
  return request({
    url: '/sysUser/SearchAllUser',
    method: 'get',
    params
  })
}

// 根据ID获取用户
export function getUserById(id) {
  return request({
    url: `/sysUser/${id}`,
    method: 'get'
  })
}

// 创建用户
export function createUser(data) {
  return request({
    url: '/sysUser',
    method: 'post',
    data
  })
}

// 更新用户
export function updateUser(data) {
  // 确保ID字段为字符串类型，符合后端预期
  if (data.id !== undefined && data.id !== null && typeof data.id !== 'string') {
    data.id = String(data.id);
  }

  // 确保deptId和postId为长整型值
  if (data.deptId !== undefined && data.deptId !== null) {
    data.deptId = Number(data.deptId);
  }

  if (data.postId !== undefined && data.postId !== null) {
    data.postId = Number(data.postId);
  }

  // 确保status为整型值
  if (data.status !== undefined && data.status !== null) {
    data.status = Number(data.status);
  }

  console.log('API发送格式化后的数据:', data);

  return request({
    url: '/sysUser',
    method: 'put',
    data,
    headers: {
      'Content-Type': 'application/json'
    },
    timeout: 15000 // 增加超时时间
  });
}

// 删除用户
export function deleteUser(id) {
  return request({
    url: `/sysUser/${id}`,
    method: 'delete'
  })
}

// 获取用户登录日志
export function getUserLoginLog(params) {
  return request({
    url: '/sysUser/getUserLoginLog',
    method: 'post',
    params
  })
}

// 获取用户操作日志
export function getUserOperLog(params) {
  return request({
    url: '/sysUser/getUserOperLog',
    method: 'post',
    params
  })
}
