mapper.select:
SELECT * FROM coupon_info
WHERE 1 = 1
AND coupon_type = #{couponType}
AND name LIKE CONCAT('%', #{name}, '%')
AND amount = #{amount}
AND discount >= #{minDiscount}
AND discount <= #{maxDiscount}
AND condition_amount >= #{minConditionAmount}
AND condition_amount <= #{maxConditionAmount}
AND status = #{status}
AND publish_count >= #{minPublishCount}
AND publish_count <= #{maxPublishCount}
AND per_limit >= #{minPerLimit}
AND per_limit <= #{maxPerLimit}
AND use_count >= #{minUseCount}
AND use_count <= #{maxUseCount}
AND receive_count >= #{minReceiveCount}
AND receive_count <= #{maxReceiveCount}
AND expire_time >= #{minExpireTime}
AND expire_time <= #{maxExpireTime}
ORDER BY id DESC
LIMIT #{offset}, #{limit}

SELECT COUNT(*) FROM coupon_info
WHERE 1 = 1
AND coupon_type = #{couponType}
AND name LIKE CONCAT('%', #{name}, '%')
AND amount = #{amount}
AND discount >= #{minDiscount}
AND discount <= #{maxDiscount}
AND condition_amount >= #{minConditionAmount}
AND condition_amount <= #{maxConditionAmount}
AND status = #{status}
AND publish_count >= #{minPublishCount}
AND publish_count <= #{maxPublishCount}
AND per_limit >= #{minPerLimit}
AND per_limit <= #{maxPerLimit}
AND use_count >= #{minUseCount}
AND use_count <= #{maxUseCount}
AND receive_count >= #{minReceiveCount}
AND receive_count <= #{maxReceiveCount}
AND expire_time >= #{minExpireTime}
AND expire_time <= #{maxExpireTime}

mapper.insert:
INSERT INTO coupon_info (
    coupon_type, name, amount, discount, condition_amount,
    publish_count, per_limit, use_count, receive_count, expire_time,
    description, status
)
VALUES (
           #{couponType}, #{name}, #{amount}, #{discount}, #{conditionAmount},
           #{publishCount}, #{perLimit}, #{useCount}, #{receiveCount}, #{expireTime},
           #{description}, #{status}
       )

mapper.select:
SELECT * FROM customer_coupon
WHERE 1 = 1
AND customer_id = #{customerId}
AND order_id = #{orderId}
AND status = #{status}
ORDER BY receive_time DESC
LIMIT #{offset}, #{limit}

SELECT COUNT(*) FROM customer_coupon
WHERE 1 = 1
AND customer_id = #{customerId}
AND order_id = #{orderId}
AND status = #{status}
