mapper.select:
select *from sys_dept
where 1=1

    and name like concat('%',#{name},'%')


    and parent_id = #{parentId}


    and children = #{children}


    and leader = #{leader}


    and status = #{status}

order by sort_value asc
limit #{start},#{pageSize}

