mapper.select:
SELECT * FROM sys_post
WHERE 1 = 1
AND name LIKE CONCAT('%', #{name}, '%')
AND post_code = #{postCode}
AND status = #{status}
ORDER BY id DESC
LIMIT #{offset}, #{limit}

SELECT COUNT(*) FROM sys_post
WHERE 1 = 1
AND name LIKE CONCAT('%', #{name}, '%')
AND post_code = #{postCode}
AND status = #{status}

mapper.update:
UPDATE sys_post
SET post_code = #{postCode},
    name = #{name},
    description = #{description},
    status = #{status}
WHERE id = #{id}
