mapper.select:
SELECT * FROM driver_info
WHERE 1 = 1
AND name LIKE CONCAT('%', #{name}, '%')
AND nickname LIKE CONCAT('%', #{nickname}, '%')
AND gender = #{gender}
AND phone = #{phone}
AND job_no = #{jobNo}
AND status = #{status}
AND auth_status = #{authStatus}
AND score >= #{minScore}
AND score <= #{maxScore}
AND order_count >= #{minOrderCount}
AND order_count <= #{maxOrderCount}
ORDER BY job_no DESC
LIMIT #{offset}, #{limit}

SELECT COUNT(*) FROM driver_info
WHERE 1 = 1
AND name LIKE CONCAT('%', #{name}, '%')
AND nickname LIKE CONCAT('%', #{nickname}, '%')
AND gender = #{gender}
AND phone = #{phone}
AND job_no = #{jobNo}
AND status = #{status}
AND auth_status = #{authStatus}
AND score >= #{minScore}
AND score <= #{maxScore}
AND order_count >= #{minOrderCount}
AND order_count <= #{maxOrderCount}

SELECT * FROM driver_account_detail
WHERE 1 = 1
AND driver_id = #{driverId}
AND trade_no = #{tradeNo}
AND trade_type = #{tradeType}
AND amount >= #{minAmount}
AND amount <= #{maxAmount}
ORDER BY trade_no DESC
LIMIT #{offset}, #{limit}

SELECT COUNT(*) FROM driver_account_detail
WHERE 1 = 1
AND driver_id = #{driverId}
AND trade_no = #{tradeNo}
AND trade_type = #{tradeType}
AND amount >= #{minAmount}
AND amount <= #{maxAmount}
