mapper.select:
SELECT * FROM order_comment
WHERE 1 = 1
AND customer_id = #{customerId}
AND driver_id = #{driverId}
AND rate = #{rate}
AND status = #{status}
ORDER BY order_id DESC
LIMIT #{offset}, #{limit}

SELECT COUNT(*) FROM order_comment
WHERE 1 = 1
AND customer_id = #{customerId}
AND driver_id = #{driverId}
AND rate = #{rate}
AND status = #{status}
