mapper.select:
SELECT * FROM sys_user
WHERE 1 = 1
AND id = #{id}
AND username = #{username}
AND name LIKE CONCAT('%', #{name}, '%')
AND phone LIKE CONCAT('%', #{phone}, '%')
AND dept_id = #{deptId}
AND post_id = #{postId}
AND status = #{status}
AND post_name LIKE CONCAT('%', #{postName}, '%')
AND dept_name LIKE CONCAT('%', #{deptName}, '%')
ORDER BY id DESC
LIMIT #{offset}, #{limit}

SELECT COUNT(*) FROM sys_user
WHERE 1 = 1
AND id = #{id}
AND username = #{username}
AND name LIKE CONCAT('%', #{name}, '%')
AND phone LIKE CONCAT('%', #{phone}, '%')
AND dept_id = #{deptId}
AND post_id = #{postId}
AND status = #{status}
AND post_name LIKE CONCAT('%', #{postName}, '%')
AND dept_name LIKE CONCAT('%', #{deptName}, '%')

mapper.update:
UPDATE sys_user
SET username = #{username}, password = #{password}, name = #{name}, phone = #{phone},
    head_url = #{headUrl}, dept_id = #{deptId}, post_id = #{postId},
    description = #{description}, status = #{status}
WHERE id = #{id}

mapper.select:
SELECT * FROM sys_login_log
WHERE 1 = 1

    AND username = #{username}


    AND access_time >= #{minAccessTime}


    AND access_time <= #{maxAccessTime}

ORDER BY access_time DESC
LIMIT #{offset}, #{limit}

SELECT COUNT(*) FROM sys_login_log
WHERE 1 = 1

    AND username = #{username}


    AND access_time >= #{minAccessTime}


    AND access_time <= #{maxAccessTime}

SELECT * FROM sys_oper_log
WHERE 1 = 1

    AND oper_name = #{operName}


    AND dept_name = #{deptName}


    AND status = #{status}


    AND operator_type = #{operatorType}


    AND business_type = #{businessType}


    AND request_method = #{requestMethod}


    AND oper_time >= #{minOperTime}


    AND oper_time <= #{maxOperTime}

ORDER BY oper_time DESC
LIMIT #{offset}, #{limit}

SELECT COUNT(*) FROM sys_oper_log
WHERE 1 = 1

    AND oper_name = #{operName}


    AND dept_name = #{deptName}


    AND status = #{status}


    AND operator_type = #{operatorType}


    AND business_type = #{businessType}


    AND request_method = #{requestMethod}


    AND oper_time >= #{minOperTime}


    AND oper_time <= #{maxOperTime}
