import request from '@/utils/request'

// 获取部门列表 - 使用getById模拟
export function getDeptTree(params) {
  // 使用getById方法，获取ID为1的部门（根部门）
  return request({
    url: '/sysDept/getById',
    method: 'get',
    params: { id: 1 }
  }).then(async response => {
    // 模拟分页数据格式
    return {
      data: {
        items: response.data ? [response.data] : [],
        total: response.data ? 1 : 0
      }
    }
  }).catch(error => {
    console.error('获取部门失败:', error)
    // 返回空结果
    return {
      data: {
        items: [],
        total: 0
      }
    }
  })
}

// 获取部门详情
export function getDeptById(id) {
  return request({
    url: '/sysDept/getById',
    method: 'get',
    params: { id }
  })
}

// 添加部门
export function addDept(data) {
  return request({
    url: '/sysDept/add',
    method: 'post',
    data
  })
}

// 更新部门
export function updateDept(data) {
  return request({
    url: '/sysDept/update',
    method: 'post',
    data
  })
}

// 删除部门
export function deleteDept(id) {
  return request({
    url: '/sysDept/delete',
    method: 'post',
    params: { id }
  })
}
