import request from '@/utils/request'

// 登录接口
export function login(data) {
  return request({
    url: '/securityLogin/login',
    method: 'post',
    params: data
  })
}

// 获取用户信息
export function getUserInfo() {
  return request({
    url: '/securityLogin/getUserInfo',
    method: 'get'
  })
}

// 获取所有用户
export function getAllUsers(params) {
  return request({
    url: '/sysUser/SearchAllUser',
    method: 'get',
    params
  })
}

// 根据ID获取用户
export function getUserById(id) {
  return request({
    url: `/sysUser/${id}`,
    method: 'get'
  })
}

// 创建用户
export function createUser(data) {
  return request({
    url: '/sysUser',
    method: 'post',
    data
  })
}

// 更新用户
export function updateUser(data) {
  // 创建最小化数据结构，只包含必要字段
  const minimalData = {
    id: data.id,
    name: data.name || '',
    phone: data.phone || '',
    status: data.status === undefined ? 1 : Number(data.status),
    description: data.description || ''
  };

  // 只有在有值时才添加可选字段
  if (data.deptId !== undefined && data.deptId !== null) {
    minimalData.deptId = Number(data.deptId);
  }

  if (data.postId !== undefined && data.postId !== null) {
    minimalData.postId = Number(data.postId);
  }

  console.log('发送最小化用户数据:', minimalData);

  // 使用PUT方法，简化数据结构
  return request({
    url: '/sysUser',
    method: 'put',
    data: minimalData,
    headers: {
      'Content-Type': 'application/json'
    },
    timeout: 20000
  });
}

// 删除用户
export function deleteUser(id) {
  return request({
    url: `/sysUser/${id}`,
    method: 'delete'
  })
}

// 获取用户登录日志 - 使用get方法并格式化参数
