  // 确保参数类型正确并且移除undefined值
  const requestParams = {
    pageNum: params.pageNum || 1,
    pageSize: params.pageSize || 10
  };

  // 仅添加有值的参数
  if (params.username) requestParams.username = params.username;
  if (params.minAccessTime) requestParams.minAccessTime = params.minAccessTime;
  if (params.maxAccessTime) requestParams.maxAccessTime = params.maxAccessTime;

  console.log('登录日志请求参数:', requestParams);

  return request({
    url: '/sysUser/getUserLoginLog',
    method: 'post',
    params: requestParams
  });
}

// 获取用户操作日志 - 使用get方法并格式化参数
export function getUserOperLog(params) {
  // 确保参数类型正确并且移除undefined值
  const requestParams = {
    pageNum: params.pageNum || 1,
    pageSize: params.pageSize || 10
  };

  // 仅添加有值的参数
  if (params.operName) requestParams.operName = params.operName;
  if (params.deptName) requestParams.deptName = params.deptName;
  if (params.status !== undefined) requestParams.status = params.status;
  if (params.operatorType) requestParams.operatorType = params.operatorType;
  if (params.businessType) requestParams.businessType = params.businessType;
  if (params.requestMethod) requestParams.requestMethod = params.requestMethod;
  if (params.minOperTime) requestParams.minOperTime = params.minOperTime;
  if (params.maxOperTime) requestParams.maxOperTime = params.maxOperTime;

  console.log('操作日志请求参数:', requestParams);

  return request({
    url: '/sysUser/getUserOperLog',
    method: 'post',
    params: requestParams
  });
