import request from '@/utils/request'

// 登录接口
export function login(data) {
  return request({
    url: '/securityLogin/login',
    method: 'post',
    params: data
  })
}

// 获取用户信息
export function getUserInfo() {
  return request({
    url: '/securityLogin/getUserInfo',
    method: 'get'
  })
}

// 获取所有用户
export function getAllUsers(params) {
  return request({
    url: '/sysUser/SearchAllUser',
    method: 'get',
    params
  })
}

// 根据ID获取用户
export function getUserById(id) {
  return request({
    url: `/sysUser/${id}`,
    method: 'get'
  })
}

// 创建用户
export function createUser(data) {
  return request({
    url: '/sysUser',
    method: 'post',
    data
  })
}

// 更新用户
export function updateUser(data) {
  // 创建完整的用户对象，确保包含所有SQL中需要的字段
  const userObj = {
    // 确保id是字符串
    id: data.id ? String(data.id) : '',
    // 保持用户名不变
    username: data.username || '',
    // 密码字段，保持不变
    password: '', // 不修改密码
    // 必填字段
    name: data.name || '',
    phone: data.phone || '',
    // 数字类型字段
    deptId: data.deptId ? Number(data.deptId) : null,
    postId: data.postId ? Number(data.postId) : null,
    // 其他必需字段
    headUrl: '', // 不修改头像
    status: data.status === undefined ? 1 : Number(data.status),
    description: data.description || ''
  };

  console.log('发送完整用户对象:', userObj);

  return request({
    url: '/sysUser',
    method: 'put',
    data: userObj,
    headers: {
      'Content-Type': 'application/json'
    },
    timeout: 20000
  });
}

// 删除用户
export function deleteUser(id) {
  return request({
    url: `/sysUser/${id}`,
    method: 'delete'
  })
}

// 获取用户登录日志
export function getUserLoginLog(params) {
  // 简化请求参数
  const requestParams = {
    pageNum: 1,
    pageSize: 10
  };

  // 仅添加用户名参数
  if (params.username) {
    requestParams.username = params.username;
  }

  console.log('登录日志请求参数:', requestParams);

  return request({
    url: '/sysUser/getUserLoginLog',
    method: 'post',
    params: requestParams
  });
}

// 获取用户操作日志
export function getUserOperLog(params) {
  // 简化请求参数
  const requestParams = {
    pageNum: 1,
    pageSize: 10
  };

  // 仅添加操作人名称
  if (params.operName) {
    requestParams.operName = params.operName;
  }

  console.log('操作日志请求参数:', requestParams);

  return request({
    url: '/sysUser/getUserOperLog',
    method: 'post',
    params: requestParams
  });
}
