import request from '@/utils/request'

// 获取部门树
export function getDeptTree(params) {
  // 尝试调用树接口
  return request({
    url: '/sysDept/tree',
    method: 'get'
  }).catch(error => {
    // 如果tree接口不存在（404），尝试回退到list接口
    if (error.response && error.response.status === 404) {
      console.log('树接口不存在，回退到list接口');
      return request({
        url: '/sysDept/list',
        method: 'get',
        params: {
          pageSize: 10, // 减小数据量
          pageNum: 1
        }
      });
    }
    return Promise.reject(error);
  });
}

// 获取部门详情
export function getDeptById(id) {
  return request({
    url: '/sysDept/getById',
    method: 'get',
    params: { id }
  })
}

// 添加部门
export function addDept(data) {
  return request({
    url: '/sysDept/add',
    method: 'post',
    data
  })
}

// 更新部门
export function updateDept(data) {
  return request({
    url: '/sysDept/update',
    method: 'post',
    data
  })
}

// 删除部门
export function deleteDept(id) {
  return request({
    url: '/sysDept/delete',
    method: 'post',
    params: { id }
  })
}
