import request from '@/utils/request'

// 获取部门树
export function getDeptTree(params) {
  // 确保提供默认的分页参数
  const defaultParams = {
    pageSize: 20,  // 减小数据量
    pageNum: 1
  }

  // 使用原始的list接口获取部门列表
  return request({
    url: '/sysDept/list',
    method: 'get',
    params: params || defaultParams
  }).catch(error => {
    console.error('获取部门列表失败，错误:', error);
    // 错误时返回空数据，避免前端崩溃
    return {
      data: {
        items: []
      }
    };
  });
}

// 获取部门详情
export function getDeptById(id) {
  return request({
    url: '/sysDept/getById',
    method: 'get',
    params: { id }
  })
}

// 添加部门
export function addDept(data) {
  return request({
    url: '/sysDept/add',
    method: 'post',
    data
  })
}

// 更新部门
export function updateDept(data) {
  return request({
    url: '/sysDept/update',
    method: 'post',
    data
  })
}

// 删除部门
export function deleteDept(id) {
  return request({
    url: '/sysDept/delete',
    method: 'post',
    params: { id }
  })
}
