import request from '@/utils/request'

// 登录接口
export function login(data) {
  return request({
    url: '/securityLogin/login',
    method: 'post',
    params: data
  })
}

// 获取用户信息
export function getUserInfo() {
  return request({
    url: '/securityLogin/getUserInfo',
    method: 'get'
  })
}

// 获取所有用户
export function getAllUsers(params) {
  return request({
    url: '/sysUser/SearchAllUser',
    method: 'get',
    params
  })
}

// 根据ID获取用户
export function getUserById(id) {
  return request({
    url: `/sysUser/${id}`,
    method: 'get'
  })
}

// 创建用户
export function createUser(data) {
  return request({
    url: '/sysUser',
    method: 'post',
    data
  })
}

// 更新用户 - 使用表单格式而非JSON (模仿CustomerInfoController)
export function updateUserForm(data) {
  // 创建表单数据对象，便于日志输出
  const formData = {
    id: data.id,
    username: data.username,
    name: data.name,
    phone: data.phone,
    status: data.status === undefined ? 1 : Number(data.status)
  };

  // 只有在有值时才添加可选字段
  if (data.deptId !== undefined && data.deptId !== null) {
    formData.deptId = Number(data.deptId);
  }

  if (data.postId !== undefined && data.postId !== null) {
    formData.postId = Number(data.postId);
  }

  if (data.description !== undefined) {
    formData.description = data.description;
  }

  console.log('发送表单格式用户数据:', formData);

  // 使用POST方法，用params发送表单数据
  return request({
    url: '/sysUser/update',
    method: 'post',
    params: formData,
    timeout: 20000
  });
}

// 删除用户
export function deleteUser(id) {
  return request({
    url: `/sysUser/${id}`,
    method: 'delete'
  })
}

// 获取用户登录日志
export function getUserLoginLog(params) {
  // 简化请求参数
  const requestParams = {
    pageNum: 1,
    pageSize: 10
  };

  // 仅添加用户名参数
  if (params.username) {
    requestParams.username = params.username;
  }

  console.log('登录日志请求参数:', requestParams);

  return request({
    url: '/sysUser/getUserLoginLog',
    method: 'post',
    params: requestParams
  });
}

// 获取用户操作日志
export function getUserOperLog(params) {
  // 简化请求参数
  const requestParams = {
    pageNum: 1,
    pageSize: 10
  };

  // 仅添加操作人名称
  if (params.operName) {
    requestParams.operName = params.operName;
  }

  console.log('操作日志请求参数:', requestParams);

  return request({
    url: '/sysUser/getUserOperLog',
    method: 'post',
    params: requestParams
  });
}
