import request from '@/utils/request'

// 获取部门树 - 使用getById代替list，避免500错误
export function getDeptTree(params) {
  // 获取根部门（假设ID=1）
  return request({
    url: '/sysDept/getById',
    method: 'get',
    params: { id: 1 }
  }).then(response => {
    console.log('获取到根部门:', response);
    // 这里我们只获取了一个部门，但至少不会500
    // 可以手动构造一个简单的部门列表
    if (response.data) {
      response.data = [response.data];
    }
    return response;
  });
}

// 获取部门详情
export function getDeptById(id) {
  return request({
    url: '/sysDept/getById',
    method: 'get',
    params: { id }
  })
}

// 添加部门
export function addDept(data) {
  return request({
    url: '/sysDept/add',
    method: 'post',
    data
  })
}

// 更新部门
export function updateDept(data) {
  return request({
    url: '/sysDept/update',
    method: 'post',
    data
  })
}

// 删除部门
export function deleteDept(id) {
  return request({
    url: '/sysDept/delete',
    method: 'post',
    params: { id }
  })
}
