import { ref, reactive, onMounted } from 'vue';
import { ElMessage, ElMessageBox } from 'element-plus';
import { getRoleList, addRole, updateRole, deleteRole } from '@/api/role';

// 查询参数

export default {
  __name: 'SysRole',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const queryParams = reactive({
      pageNum: 1,
      pageSize: 10,
      roleName: '',
      roleCode: ''
    });

    // 表格数据和状态
    const roleList = ref([]);
    const loading = ref(false);
    const total = ref(0);

    // 获取角色列表
    const getList = async () => {
      try {
        loading.value = true;
        const res = await getRoleList(queryParams);
        console.log('角色列表响应:', res);
        roleList.value = res.data.items || [];
        total.value = res.data.total || 0;
      } catch (error) {
        console.error('获取角色列表失败', error);
      } finally {
        loading.value = false;
      }
    };

    // 新增角色
    const handleAdd = () => {
      ElMessage.info('新增角色功能待实现');
    };

    // 编辑角色
    const handleEdit = row => {
      ElMessage.info(`编辑角色: ${row.roleName}`);
    };

    // 删除角色
    const handleDelete = row => {
      ElMessageBox.confirm(`确定要删除角色 ${row.roleName} 吗?`, '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(async () => {
        try {
          await deleteRole(row.id);
          ElMessage.success('删除成功');
          getList();
        } catch (error) {
          ElMessage.error('删除失败');
        }
      }).catch(() => {});
    };

    // 分页操作
    const handleSizeChange = size => {
      queryParams.pageSize = size;
      getList();
    };
    const handleCurrentChange = page => {
      queryParams.pageNum = page;
      getList();
    };

    // 页面加载时获取数据
    onMounted(() => {
      getList();
    });
    const __returned__ = {
      queryParams,
      roleList,
      loading,
      total,
      getList,
      handleAdd,
      handleEdit,
      handleDelete,
      handleSizeChange,
      handleCurrentChange,
      ref,
      reactive,
      onMounted,
      get ElMessage() {
        return ElMessage;
      },
      get ElMessageBox() {
        return ElMessageBox;
      },
      get getRoleList() {
        return getRoleList;
      },
      get addRole() {
        return addRole;
      },
      get updateRole() {
        return updateRole;
      },
      get deleteRole() {
        return deleteRole;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
